import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [query, setQuery] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(query); 
  };
  return (
    <div className="App">
      <h1>Image Slider</h1>
      <form onSubmit={handleSubmit}> 
        <input type="text" onChange={(e) => setQuery(e.target.value)} /> 
        <input type="submit" value="Search" />
      </form>
    </div>
  );
}

export default App;
