import React, { Component } from 'react';

class App extends React.Component {
  state = {
    name: ''
  }
  range = (eve) => {
    this.state.name = eve.target.value
    console.log(this.state.name)
  }
  render() {
    return (
      <div>
        <h1>Hello, world!{this.state.name}</h1>
        <input className="no-border" onChange={this.range} type="range" min={0} max={30} />
      </div>
    );
  }
}
export default App;
